// src/Dashboard.jsx
import React, { useEffect, useState } from 'react';
import { db, auth } from './firebase';
import { doc, getDoc, collection, getDocs } from 'firebase/firestore';
import { useNavigate } from 'react-router-dom';
import './AdminInterface.css';

const Dashboard = () => {
  const [data, setData] = useState(null);
  const [username, setUsername] = useState('');
  const [openSession, setOpenSession] = useState(null);
  const navigate = useNavigate();

  // 1️⃣ Auth guard + load user data
  useEffect(() => {
    const unsub = auth.onAuthStateChanged(async user => {
      if (!user) {
        navigate('/', { replace: true });
        return;
      }
      const email = user.email;
      const uname = email.split('@')[0];
      setUsername(uname);

      const docRef = doc(db, 'users', uname);
      const docSnap = await getDoc(docRef);
      setData(docSnap.exists()
        ? docSnap.data()
        : { absences: 0, attendance: [] }
      );
    });
    return unsub;
  }, [navigate]);

  // 2️⃣ Check for an open attendanceSession
  useEffect(() => {
    const loadSession = async () => {
      const sessSnap = await getDocs(collection(db, 'attendanceSessions'));
      const open = sessSnap.docs
        .map(d => ({ id: d.id, ...d.data() }))
        .find(s => s.open);
      setOpenSession(open || null);
    };
    loadSession();
  }, []);

  return (
    <div className="admin-container">
      <h2 className="admin-title">Welcome, {username}</h2>

      {!data ? (
        <p>Loading…</p>
      ) : (
        <>
          <p>Total Absences: {data.absences}</p>

          <h3>Attendance Record:</h3>
          <ul>
            {data.attendance.map((entry, idx) => (
              <li key={idx}>{new Date(entry).toLocaleString()}</li>
            ))}
          </ul>

          <div className="input-group" style={{ justifyContent: 'center' }}>
            <button
              className="admin-btn"
              onClick={() => navigate('/qr')}
              disabled={!openSession}
            >
              Scan QR to Check In
            </button>
          </div>

          {!openSession && (
            <p style={{ textAlign: 'center', color: '#a31f34' }}>
              No session currently open.
            </p>
          )}
        </>
      )}
    </div>
  );
};

export default Dashboard;
