// QRScanner.js
import React, { useState } from 'react';
import QrReader from 'react-qr-reader';

const QRScanner = () => {
    const [scanResult, setScanResult] = useState('');
    const validQRCode = "MEETING_2025_05_11"; // Replace with dynamic meeting code later

    const handleScan = data => {
        if (data) {
            setScanResult(data);
            if (data === validQRCode) {
                alert("Attendance recorded!");
                // Save attendance in Firestore or Realtime Database
            } else {
                alert("Invalid QR code.");
            }
        }
    };

    const handleError = err => {
        console.error(err);
    };

    return (
        <div>
            <h2>Scan QR Code to Sign In</h2>
            <QrReader
                delay={300}
                onError={handleError}
                onScan={handleScan}
                style={{ width: '100%' }}
            />
            {scanResult && <p>Scanned: {scanResult}</p>}
        </div>
    );
};

export default QRScanner;
