// src/ScanQRCode.jsx
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import QrReader from 'modern-react-qr-reader';
import { auth, db } from './firebase';
import { doc, getDoc, updateDoc, arrayUnion } from 'firebase/firestore';
import './AdminInterface.css';

const ScanQRCode = () => {
  const [error, setError] = useState('');
  const navigate = useNavigate();

  // redirect if not logged in
  useEffect(() => {
    if (!auth.currentUser) {
      navigate('/');
    }
  }, [navigate]);

  const handleScan = async (data) => {
    if (!data) return;
    const sessionId = data;
    const sessRef = doc(db, 'attendanceSessions', sessionId);
    const sessSnap = await getDoc(sessRef);
    if (!sessSnap.exists() || !sessSnap.data().open) {
      setError('No active session with that code.');
      return;
    }

    const email = auth.currentUser.email;
    const username = email.split('@')[0];
    const userRef = doc(db, 'users', username);

    // atomically append to attendance array
    await updateDoc(userRef, {
      attendance: arrayUnion(sessionId),
      absences: (await getDoc(userRef)).data().attendance?.length || 0
    });

    navigate('/dashboard');
  };

  const handleError = (err) => {
    console.error(err);
    setError('Camera error: ' + err.message);
  };

  return (
    <div className="admin-container">
      <button
        className="admin-btn"
        style={{ marginBottom: '1rem' }}
        onClick={() => navigate(-1)}
      >
        ‚Üê Back
      </button>

      <h2 className="admin-title">Scan QR Code to Check In</h2>

      <div className="qr-container">
        <QrReader
          delay={300}
          facingMode="environment"
          onError={handleError}
          onScan={handleScan}
          constraints={{ facingMode: 'environment' }}
          style={{ width: '100%' }}
          showViewFinder={true}
        />
      </div>

      {error && <div className="error">{error}</div>}
    </div>
  );
};

export default ScanQRCode;
