import React, { useEffect, useState } from 'react';
import { initializeApp } from 'firebase/app';
import { getAnalytics } from 'firebase/analytics';
import { getFirestore, collection, getDocs } from 'firebase/firestore';
import './AttendanceChart.css';

// Your web app's Firebase configuration
const firebaseConfig = {
  apiKey: "AIzaSyCEGCSkc0mSAvn4mB5mQXTTDZ3GcEScHWg",
  authDomain: "attendance-f524b.firebaseapp.com",
  projectId: "attendance-f524b",
  storageBucket: "attendance-f524b.firebasestorage.app",
  messagingSenderId: "295378375527",
  appId: "1:295378375527:web:ac2b55cb9a917ba87c55c2",
  measurementId: "G-124MCMCKTR"
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);
getAnalytics(app);
const db = getFirestore(app);

const AttendanceChart = () => {
  const [users, setUsers] = useState([]);
  const [dates, setDates] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const snap = await getDocs(collection(db, 'users'));
      const data = snap.docs.map(d => ({ id: d.id, ...d.data() }));
      setUsers(data);

      const dateSet = new Set();
      data.forEach(u => {
        (u.attendance || []).forEach(date => dateSet.add(date));
      });

      const sortedDates = Array.from(dateSet).sort();
      setDates(sortedDates);
    };
    fetchData();
  }, []);

  return (
    <div className="attendance-container">
      <h2 className="attendance-title">Attendance Chart</h2>
      <div className="attendance-table-wrapper">
        <table className="attendance-table">
          <thead>
            <tr>
              <th>Name</th>
              {dates.map(date => (
                <th key={date}>{new Date(date).toLocaleDateString()}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {users.map(user => (
              <tr key={user.id}>
                <td>{user.id}</td>
                {dates.map(date => (
                  <td key={date}>
                    {user.attendance?.includes(date) ? '✓' : ''}
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default AttendanceChart;
