// src/ScanQRCode.jsx
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { QrReader } from 'react-qr-reader';
import { auth, db } from './firebase';
import { doc, getDoc, updateDoc } from 'firebase/firestore';
import './AdminInterface.css';

const ScanQRCode = () => {
  const [error, setError] = useState('');
  const navigate = useNavigate();

  // Redirect to login if not signed in
  useEffect(() => {
    if (!auth.currentUser) {
      navigate('/');
    }
  }, [navigate]);

  const handleResult = async (result, err) => {
    if (result) {
      const sessionId = result?.text ?? result;
      // fetch session and ensure it's open
      const sessRef = doc(db, 'attendanceSessions', sessionId);
      const sessSnap = await getDoc(sessRef);
      if (!sessSnap.exists() || !sessSnap.data().open) {
        setError('No active session with that code.');
        return;
      }
      // mark user present
      const email = auth.currentUser.email;
      const username = email.split('@')[0];
      const userRef = doc(db, 'users', username);
      // append to attendance array (use arrayUnion if you'd like)
      await updateDoc(userRef, {
        attendance: [...( (await getDoc(userRef)).data().attendance || [] ), sessionId]
      });
      navigate('/dashboard');
    }
    if (err) {
      console.error(err);
    }
  };

  return (
    <div className="admin-container">
      <button
        className="admin-btn"
        style={{ marginBottom: '1rem' }}
        onClick={() => navigate(-1)}
      >
        ‚Üê Back
      </button>

      <h2 className="admin-title">Scan QR Code to Check In</h2>

      <div className="qr-container">
        <QrReader
          constraints={{ facingMode: 'environment' }}
          onResult={handleResult}
          style={{ width: '100%' }}
        />
      </div>

      {error && <div className="error">{error}</div>}
    </div>
  );
};

export default ScanQRCode;
