import React, { useState } from "react";
import { initializeApp } from "firebase/app";
import { getAuth, createUserWithEmailAndPassword, updateProfile } from "firebase/auth";
import { getAnalytics } from "firebase/analytics";
import "./StudentInterface.css"; // Reuse voting app styles

// --- Firebase configuration ---
const firebaseConfig = {
  apiKey: "AIzaSyCEGCSkc0mSAvn4mB5mQXTTDZ3GcEScHWg",
  authDomain: "attendance-f524b.firebaseapp.com",
  projectId: "attendance-f524b",
  storageBucket: "attendance-f524b.firebasestorage.app",
  messagingSenderId: "295378375527",
  appId: "1:295378375527:web:ac2b55cb9a917ba87c55c2",
  measurementId: "G-124MCMCKTR"
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);
getAnalytics(app);
const auth = getAuth(app);

const Signup = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [success, setSuccess] = useState(false);

  const handleSignup = async (e) => {
    e.preventDefault();
    setError("");
    if (!username.trim() || !password) {
      setError("Both username and password are required.");
      return;
    }

    try {
      // In this example, treat username as email
      const email = `${username.trim()}@school.edu`;
      const userCredential = await createUserWithEmailAndPassword(auth, email, password);
      // Optionally set displayName
      await updateProfile(userCredential.user, {
        displayName: username.trim()
      });

      setSuccess(true);
    } catch (err) {
      setError(err.message);
    }
  };

  if (success) {
    return (
      <div className="student-container">
        <h2 className="student-title">Signup Successful</h2>
        <p>Your account <strong>{username}</strong> has been created. Please log in to continue.</p>
      </div>
    );
  }

  return (
    <div className="student-container">
      <h2 className="student-title">Student Council Signup</h2>
      <form className="vote-form" onSubmit={handleSignup}>
        <div className="input-group">
          <label>Username:</label>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="e.g. jsmith"
          />
        </div>
        <div className="input-group">
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Enter a secure password"
          />
        </div>
        {error && <div className="error-message">{error}</div>}
        <button type="submit">Sign Up</button>
      </form>
    </div>
  );
};

export default Signup;
