import React, { useState } from 'react';
import {
  createUserWithEmailAndPassword,
  signInWithEmailAndPassword
} from 'firebase/auth';
import { doc, setDoc } from 'firebase/firestore';
import { useNavigate } from 'react-router-dom';
import { auth, db } from './firebase';
import './StudentInterface.css';

const AuthPage = () => {
  const [mode, setMode] = useState('login');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirm, setShowConfirm] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const navigate = useNavigate();

  const emailFromUsername = (u) => `${u.trim()}@stuco.local`;

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    const email = emailFromUsername(username);
    if (!username) {
      setError('Please enter a username.');
      return;
    }

    if (mode === 'signup') {
      if (password !== confirmPassword) {
        setError('Passwords do not match.');
        return;
      }
      try {
        await createUserWithEmailAndPassword(auth, email, password);
        await setDoc(doc(db, 'users', username), {
          absences: 0,
          attendance: []
        });
        setSuccess('Account created! Redirecting...');
        setTimeout(() => navigate('/dashboard'), 1000);
      } catch (err) {
        setError(err.message);
      }
    } else {
      try {
        await signInWithEmailAndPassword(auth, email, password);
        setSuccess('Logged in! Redirecting...');
        setTimeout(() => navigate('/dashboard'), 500);
      } catch (err) {
        setError(err.message);
      }
    }
  };

  return (
    <div className="student-container">
      <h2 className="student-title">
        {mode === 'login' ? 'Student Login' : 'Sign Up'}
      </h2>
      <form className="vote-form" onSubmit={handleSubmit}>
        <div className="input-group">
          <label>Username</label>
          <input
            type="text"
            value={username}
            placeholder="just the name, no @stuco.local"
            onChange={e => setUsername(e.target.value)}
            required
            disabled={!!success}
          />
        </div>

        <div className="input-group" style={{ position: 'relative' }}>
          <label>Password</label>
          <input
            type={showPassword ? 'text' : 'password'}
            value={password}
            onChange={e => setPassword(e.target.value)}
            required
            disabled={!!success}
          />
          <span
            onClick={() => setShowPassword(v => !v)}
            style={{
              position: 'absolute',
              right: 10,
              top: '50%',
              transform: 'translateY(-50%)',
              cursor: 'pointer'
            }}
            aria-label={showPassword ? 'Hide password' : 'Show password'}
          >
            {showPassword ? 'üôà' : 'üëÅÔ∏è'}
          </span>
        </div>

        {mode === 'signup' && (
          <div className="input-group" style={{ position: 'relative' }}>
            <label>Confirm Password</label>
            <input
              type={showConfirm ? 'text' : 'password'}
              value={confirmPassword}
              onChange={e => setConfirmPassword(e.target.value)}
              required
              disabled={!!success}
            />
            <span
              onClick={() => setShowConfirm(v => !v)}
              style={{
                position: 'absolute',
                right: 10,
                top: '50%',
                transform: 'translateY(-50%)',
                cursor: 'pointer'
              }}
              aria-label={showConfirm ? 'Hide confirm password' : 'Show confirm password'}
            >
              {showConfirm ? 'üôà' : 'üëÅÔ∏è'}
            </span>
          </div>
        )}

        {error && <div className="error">{error}</div>}
        {success && <div className="success">{success}</div>}

        <button type="submit" className="admin-btn">
          {mode === 'login' ? 'Log In' : 'Create Account'}
        </button>
      </form>

      <div style={{ marginTop: 16 }}>
        {mode === 'login' ? (
          <>
            <span style>Need an account? </span>
            <button
              className="link-btn"
              onClick={() => {
                setMode('signup');
                setError('');
                setSuccess('');
              }}
            >
              Sign Up
            </button>
          </>
        ) : (
          <>
            <span>Already have one? </span>
            <button
              className="link-btn"
              onClick={() => {
                setMode('login');
                setError('');
                setSuccess('');
              }}
            >
              Log In
            </button>
          </>
        )}
      </div>
    </div>
  );
};

export default AuthPage;
