// src/Dashboard.jsx
import React, { useEffect, useState } from 'react';
import { db, auth } from './firebase';
import { doc, getDoc } from 'firebase/firestore';
import { useNavigate } from 'react-router-dom';
import './AdminInterface.css';

const Dashboard = () => {
  const [data, setData] = useState(null);
  const [username, setUsername] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    // Listen for auth state changes
    const unsubscribe = auth.onAuthStateChanged(async user => {
      if (!user) {
        // not signed in → back to login
        navigate('/', { replace: true });
        return;
      }
      // signed in → load their user doc
      const email = user.email;
      const uname = email.split('@')[0];
      setUsername(uname);

      const docRef = doc(db, 'users', uname);
      const docSnap = await getDoc(docRef);

      if (docSnap.exists()) {
        setData(docSnap.data());
      } else {
        setData({ absences: 0, attendance: [] });
      }
    });

    return unsubscribe;
  }, [navigate]);

  return (
    <div className="admin-container">
      <h2 className="admin-title">Welcome, {username}</h2>

      {!data ? (
        <p>Loading…</p>
      ) : (
        <>
          <p>Total Absences: {data.absences}</p>

          <h3>Attendance Record:</h3>
          <ul>
            {data.attendance.map((entry, idx) => (
              <li key={idx}>
                {new Date(entry).toLocaleString()}
              </li>
            ))}
          </ul>

          <div className="input-group" style={{ justifyContent: 'center' }}>
            <button
              className="admin-btn"
              onClick={() => navigate('/qr')}
            >
              Scan QR to Check In
            </button>
          </div>
        </>
      )}
    </div>
  );
};

export default Dashboard;
