// src/ScanQRCode.jsx
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { QrReader } from 'modern-react-qr-reader';
import { auth, db } from './firebase';
import { doc, getDoc, updateDoc, arrayUnion } from 'firebase/firestore';
import './AdminInterface.css';

const ScanQRCode = () => {
  const [error, setError] = useState('');
  const navigate = useNavigate();

  // Redirect to login if not signed in
  useEffect(() => {
    if (!auth.currentUser) navigate('/');
  }, [navigate]);

  // Prompt for camera access once
  useEffect(() => {
    navigator.mediaDevices
      .getUserMedia({ video: { facingMode: 'environment' } })
      .catch(err => {
        console.error('Camera permission error:', err);
        setError('Camera access is required to scan QR codes.');
      });
  }, []);

  const handleResult = async (result, err) => {
    if (result) {
      const sessionId = result?.text ?? result;
      const sessRef = doc(db, 'attendanceSessions', sessionId);
      const sessSnap = await getDoc(sessRef);

      if (!sessSnap.exists() || !sessSnap.data().open) {
        setError('No active session with that code.');
        return;
      }

      // mark user present
      const email = auth.currentUser.email;
      const username = email.split('@')[0];
      const userRef = doc(db, 'users', username);

      await updateDoc(userRef, {
        attendance: arrayUnion(sessionId)
      });

      navigate('/dashboard');
    }
    if (err) {
      console.error(err);
      // you could setError('Error scanning QR code') here if desired
    }
  };

  return (
    <div className="admin-container">
      <button
        className="admin-btn"
        style={{ marginBottom: '1rem' }}
        onClick={() => navigate(-1)}
      >
        ‚Üê Back
      </button>

      <h2 className="admin-title">Scan QR Code to Check In</h2>

      {error && (
        <div className="error" style={{ marginBottom: '1rem' }}>
          {error}
        </div>
      )}

      <div className="qr-container" style={{ position: 'relative', width: '100%', paddingTop: '100%' }}>
        <QrReader
          constraints={{ video: { facingMode: 'environment' } }}
          onResult={handleResult}
          containerStyle={{ position: 'absolute', top: 0, left: 0, width: '100%', height: '100%' }}
          videoStyle={{ width: '100%', height: '100%', objectFit: 'cover' }}
          videoContainerStyle={{ width: '100%', height: '100%' }}
        />
      </div>
    </div>
  );
};

export default ScanQRCode;
