import React, { useState } from 'react';
import QRCode from 'qrcode.react';
import './AdminInterface.css';

const AdminQRCode = () => {
  const [inputStr, setInputStr] = useState('');
  const [qrValue, setQrValue] = useState('');

  const handleGenerate = () => {
    if (!inputStr.trim()) return;
    setQrValue(inputStr.trim());
  };

  return (
    <div className="admin-container">
      <h2 className="admin-title">QR Code Generator</h2>
      <div className="input-group">
        <label htmlFor="qr-input">Enter string to encode:</label>
        <input
          id="qr-input"
          type="text"
          value={inputStr}
          onChange={e => setInputStr(e.target.value)}
          placeholder="e.g. https://example.com"
        />
      </div>
      <button className="admin-btn generate-btn" onClick={handleGenerate}>
        Generate QR Code
      </button>
      {qrValue && (
        <div className="qr-display">
          <QRCode value={qrValue} size={256} level="H" includeMargin={true} />
          <p className="qr-text">{qrValue}</p>
        </div>
      )}
    </div>
  );
};

export default AdminQRCode;
