// src/ScanQRCode.jsx
import React, { useState, useEffect, useCallback, useMemo } from 'react';
import { useNavigate } from 'react-router-dom';
import QrReader from 'modern-react-qr-reader';
import { auth, db } from './firebase';
import { doc, getDoc, updateDoc, arrayUnion } from 'firebase/firestore';
import './AdminInterface.css';

const ScanQRCode = () => {
  const [error, setError] = useState('');
  const [hasPermission, setHasPermission] = useState<null | boolean>(null);
  const navigate = useNavigate();

  // redirect if not logged in
  useEffect(() => {
    if (!auth.currentUser) {
      navigate('/');
    }
  }, [navigate]);

  // ask for camera permission on mount
  useEffect(() => {
    if (!navigator.mediaDevices || !navigator.mediaDevices.getUserMedia) {
      setError('Camera API not supported on this device.');
      setHasPermission(false);
      return;
    }
    navigator.mediaDevices
      .getUserMedia({ video: { facingMode: 'environment' } })
      .then(stream => {
        setHasPermission(true);
        // stop the camera immediately, QrReader will re-open it
        stream.getTracks().forEach(track => track.stop());
      })
      .catch(err => {
        console.error('Permission error:', err);
        setError('Camera permission denied.');
        setHasPermission(false);
      });
  }, []);

  // stable scan handler
  const handleScan = useCallback(
    async data => {
      if (!data) return;
      const sessionId = data.trim();
      const sessRef = doc(db, 'attendanceSessions', sessionId);
      const sessSnap = await getDoc(sessRef);
      if (!sessSnap.exists() || !sessSnap.data().open) {
        setError('No active session with that code.');
        return;
      }

      const email = auth.currentUser.email;
      const username = email.split('@')[0];
      const userRef = doc(db, 'users', username);

      await updateDoc(userRef, {
        attendance: arrayUnion(sessionId)
      });

      navigate('/dashboard');
    },
    [navigate]
  );

  // stable error handler
  const handleError = useCallback(err => {
    console.error(err);
    setError('Camera error: ' + err.message);
  }, []);

  // memoize constraints so they never change identity
  const videoConstraints = useMemo(
    () => ({ facingMode: 'environment' }),
    []
  );

  return (
    <div className="admin-container">
      <button
        className="admin-btn"
        style={{ marginBottom: '1rem' }}
        onClick={() => navigate(-1)}
      >
        ← Back
      </button>

      <h2 className="admin-title">Scan QR Code to Check In</h2>

      <div className="qr-container">
        {hasPermission === null && (
          <p>Requesting camera access…</p>
        )}
        {hasPermission === false && (
          <div>
            <p>{error || 'Cannot access camera.'}</p>
            <button
              className="admin-btn"
              onClick={() => {
                setError('');
                setHasPermission(null);
                // retry
                navigator.mediaDevices
                  .getUserMedia({ video: { facingMode: 'environment' } })
                  .then(stream => {
                    setHasPermission(true);
                    stream.getTracks().forEach(t => t.stop());
                  })
                  .catch(err => {
                    console.error(err);
                    setError('Camera permission denied.');
                    setHasPermission(false);
                  });
              }}
            >
              Retry
            </button>
          </div>
        )}
        {hasPermission === true && (
          <QrReader
            delay={300}
            onError={handleError}
            onScan={handleScan}
            constraints={{ video: videoConstraints }}
            style={{ width: '100%' }}
            showViewFinder
          />
        )}
      </div>

      {error && hasPermission && (
        <div className="error" style={{ marginTop: '1rem' }}>{error}</div>
      )}
    </div>
  );
};

export default ScanQRCode;
