import React, { useEffect, useState } from 'react';
import { db } from './firebase';
import { collection, getDocs, addDoc, serverTimestamp } from 'firebase/firestore';

const AdminDashboard = () => {
  const [sessions, setSessions] = useState([]);

  useEffect(() => {
    const load = async () => {
      const snap = await getDocs(collection(db, 'attendanceSessions'));
      setSessions(snap.docs.map(d => d.data()));
    };
    load();
  }, []);

  const startSession = async () => {
    await addDoc(collection(db, 'attendanceSessions'), { date: serverTimestamp() });
  };

  return (
    <div className="admin-container">
      <h2 className="admin-title">Admin Dashboard</h2>
      <button
        className="admin-btn"
        onClick={startSession}
        disabled={sessions.length > 0}
      >
        {sessions.length > 0 ? 'Session In Progress' : 'Start New Session'}
      </button>

    </div>
  );
};

export default AdminDashboard;
