import React, { useEffect, useState } from 'react';
import { db } from './firebase';                   // ← use the singleton firebase.js
import { collection, getDocs } from 'firebase/firestore';
import './AttendanceChart.css';

const AttendanceChart = () => {
  const [users, setUsers] = useState([]);
  const [dates, setDates] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      // grab every user document
      const snap = await getDocs(collection(db, 'users'));
      const data = snap.docs.map(d => ({ id: d.id, ...d.data() }));
      setUsers(data);

      // collect all dates into a sorted array
      const dateSet = new Set();
      data.forEach(u => {
        (u.attendance || []).forEach(date => dateSet.add(date));
      });
      setDates(Array.from(dateSet).sort());
    };

    fetchData();
  }, []);

  return (
    <div className="attendance-container">
      <h2 className="attendance-title">Attendance Chart</h2>

      <div className="attendance-table-wrapper">
        <table className="attendance-table">
          <thead>
            <tr>
              <th>Name</th>
              {dates.map(date => (
                <th key={date}>{new Date(date).toLocaleDateString()}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {users.map(user => (
              <tr key={user.id}>
                <td className="name-cell">{user.id}</td>
                {dates.map(date => (
                  <td key={date}>
                    {user.attendance?.includes(date) ? '✓' : ''}
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default AttendanceChart;
