// src/ScanQRCode.jsx
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import QrReader from 'react-qr-reader';
import { db, auth } from './firebase';
import {
  collection,
  getDocs,
  doc,
  updateDoc,
  arrayUnion
} from 'firebase/firestore';
import './AdminInterface.css';

const ScanQRCode = () => {
  const navigate = useNavigate();
  const [session, setSession] = useState(null);
  const [message, setMessage] = useState('');

  // 1️⃣ Find the open session
  useEffect(() => {
    const loadSession = async () => {
      const sessSnap = await getDocs(collection(db, 'attendanceSessions'));
      const open = sessSnap.docs
        .map(d => ({ id: d.id, ...d.data() }))
        .find(s => s.open);
      if (!open) {
        setMessage('No session is currently open.');
      }
      setSession(open || null);
    };
    loadSession();
  }, []);

  // 2️⃣ On successful scan
  const handleScan = async data => {
    if (!data || !session) return;
    if (data !== session.id) {
      setMessage('❌ Invalid QR code');
      return;
    }

    try {
      // mark present
      const email = auth.currentUser.email;
      const uname = email.split('@')[0];
      const userRef = doc(db, 'users', uname);
      await updateDoc(userRef, {
        attendance: arrayUnion(session.id)
      });

      setMessage('✅ You are now marked present!');
      setTimeout(() => navigate('/dashboard'), 2000);
    } catch (err) {
      console.error(err);
      setMessage('Error marking attendance.');
    }
  };

  const handleError = err => {
    console.error(err);
    setMessage('Error accessing camera.');
  };

  if (message && !session) {
    return (
      <div className="admin-container">
        <h2 className="admin-title">Scan QR Code</h2>
        <p>{message}</p>
        <button className="admin-btn" onClick={() => navigate('/dashboard')}>
          ← Back
        </button>
      </div>
    );
  }

  return (
    <div className="admin-container">
      <button
        className="admin-btn"
        style={{ marginBottom: '1rem' }}
        onClick={() => navigate('/dashboard')}
      >
        ← Back to Dashboard
      </button>
      <h2 className="admin-title">Scan QR Code</h2>
      <div style={{ textAlign: 'center' }}>
        <QrReader
          delay={300}
          onError={handleError}
          onScan={handleScan}
          style={{ width: '100%' }}
        />
      </div>
      {message && (
        <p style={{ textAlign: 'center', marginTop: '1rem' }}>{message}</p>
      )}
    </div>
  );
};

export default ScanQRCode;
