import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { doc, getDoc, updateDoc } from 'firebase/firestore';
import { QRCodeCanvas as QRCode } from 'qrcode.react';
import { db } from './firebase';
import './AdminInterface.css';

const AttendanceSession = () => {
  const { sessionId } = useParams();
  const navigate = useNavigate();
  const [session, setSession] = useState(null);

  useEffect(() => {
    const loadSession = async () => {
      const snap = await getDoc(doc(db, 'attendanceSessions', sessionId));
      if (snap.exists()) {
        setSession({ id: snap.id, ...snap.data() });
      } else {
        alert('Session not found');
        navigate('/admin');
      }
    };
    loadSession();
  }, [sessionId, navigate]);

  const endSession = async () => {
    await updateDoc(doc(db, 'attendanceSessions', sessionId), {
      open: false
    });
    setSession(prev => ({ ...prev, open: false }));
  };

  if (!session) return <div>Loading‚Ä¶</div>;

  return (
    <div className="admin-container">
      <h2 className="admin-title">Today's Attendance Session</h2>

      {session.open ? (
        <div className="qr-container">
          <QRCode value={sessionId} size={256} />
          <div className="qr-value">
            Session ID: {sessionId}
            <br />
            Started:{' '}
            {session.createdAt?.toDate
              ? session.createdAt.toDate().toLocaleString()
              : new Date(session.createdAt).toLocaleString()}
          </div>
          <button className="admin-btn" onClick={endSession}>
            üõë End Session
          </button>
        </div>
      ) : (
        <div className="input-group" style={{ justifyContent: 'center' }}>
          <div>Session has ended.</div>
          <button
            className="admin-btn"
            onClick={() => navigate('/admin')}
          >
            ‚Üê Back to Dashboard
          </button>
        </div>
      )}
    </div>
  );
};

export default AttendanceSession;
