// src/AdminDashboard.jsx

import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { db } from './firebase';
import {
  collection,
  getDocs,
  addDoc,
  serverTimestamp
} from 'firebase/firestore';
import './AdminInterface.css';

const AdminDashboard = () => {
  const navigate = useNavigate();
  const [sessions, setSessions] = useState([]);

  useEffect(() => {
    const loadSessions = async () => {
      const snap = await getDocs(collection(db, 'attendanceSessions'));
      // Map each session to include its id and date (as JS Date)
      const sessData = snap.docs.map(docSnap => {
        const data = docSnap.data();
        return {
          id: docSnap.id,
          date: data.date?.toDate?.() ?? new Date(data.date)
        };
      });
      setSessions(sessData);
    };
    loadSessions();
  }, []);

  const startSession = async () => {
    // Create a new attendance session with server timestamp
    await addDoc(collection(db, 'attendanceSessions'), {
      date: serverTimestamp()
    });
    // Reload sessions so the button becomes disabled
    const snap = await getDocs(collection(db, 'attendanceSessions'));
    setSessions(snap.docs.map(docSnap => ({ id: docSnap.id })));
  };

  return (
    <div className="admin-container">
      <h2 className="admin-title">Admin Dashboard</h2>

      <div className="input-group" style={{ justifyContent: 'center' }}>
        <button
          className="admin-btn"
          onClick={startSession}
          disabled={sessions.length > 0}
        >
          {sessions.length > 0 ? 'Session In Progress' : 'Start New Session'}
        </button>
      </div>

      <div className="input-group" style={{ justifyContent: 'center' }}>
        <button
          className="admin-btn"
          onClick={() => navigate('/attendance/chart')}
        >
          ðŸ“Š View Total Attendance
        </button>
      </div>
    </div>
  );
};

export default AdminDashboard;
